function Login(props) {
   const [formValue, setFormValue] = useState({
      userEmail: '',
      userPassword: ''
    });

    const navigate = useNavigate();

    const handleChange = (e) => {
      const {name, value} = e.target;

      setFormValue({
        ...formValue,
        [name]: value
      });
    };

    const handleSubmit = (e) => {

      e.preventDefault();

      // здесь обработчик регистрации
      const {userEmail, userPassword} = formValue;
      console.log(userEmail, userPassword);

      mestoAuth.register(userEmail, userPassword)
        .then(() => {
          navigate('/signin', {replace: true});
        })
        .catch((err) => {
          console.log(err);
        })
    }

   return (
      <section className="sign-up">
        <form className="sign-up__form">
          <h2 className="sign-up__title">
            Вход
          </h2>
          <input id="sign-up-email-input" name="sign-up-email" type="email" placeholder="Email" className="sign-up__input" required />
          <input id="sign-up-password-input" name="sign-up-password" type="password" placeholder="Пароль" className="sign-up__input sign-up__input_password" required />
          <button type="submit" className="sign-up__submit" >Войти</button>
          {/* <p className="sign-up__text">
            Уже зарегистрированы? <a href="#" className="sign-up__link" >Войти</a>
          </p> */}
        </form>
      </section>
    );
  }

  export default Login;
