import closePopupButton from '../images/button__cross.svg'

import './App.css';


function PopupWithForm(props) {
    props
  return (
        <div className={`popup popup_type_${props.name}`}>
          <div className="popup__container">
              <h2 className="popup__header">{props.title}</h2>
              <form name={`popup-${props.name}`} className={`popup__form popup__form_type_${props.name}`} noValidate>
                  {props.children}
              </form>
              <button className="popup__close popup__close_place_edit item-animation"><img src={closePopupButton}
                      alt="закрыть" className="popup__close-cross" /></button>
          </div>
        </div>

  );
}

export default PopupWithForm;
