import profileEditButton from '../images/profile__edit-button.svg';
import profileAddButton from '../images/button__add.svg';

function Main() {

    function handleEditProfileClick() {
      const editButtonOpen = document.querySelector('.profile__edit-button');
      editButtonOpen.addEventListener('click', () => {
        document.querySelector('.popup_type_edit').classList.add('popup_opened');
      })
    };

    function handleEditProfileClick() {
        const editButtonOpen = document.querySelector('.profile__edit-button');
        editButtonOpen.addEventListener('click', () => {
          document.querySelector('.popup_type_edit').classList.add('popup_opened');
        })
      };

    function handleEditAvatarClick() {
        const avatarButtonOpen = document.querySelector('.profile__avatar-container');
        avatarButtonOpen.addEventListener('click', () => {
          document.querySelector('.popup_type_upload').classList.add('popup_opened');
        })
      };

      function handleAddPlaceClick() {
        const editButtonOpen = document.querySelector('.profile__edit-button');
        editButtonOpen.addEventListener('click', () => {
          document.querySelector('.popup_type_edit').classList.add('popup_opened');
        })
      };


  return (
        <main>
          <section className="profile">
              <div className="profile__avatar-container">
                  <img src="#" alt="аватар пользователя" className="profile__avatar"/>
              </div>
              <div className="profile__info">
                  <div className="profile__name-container">
                      <h1 className="profile__name">Жак-Ив Кусто</h1>
                      <button type="button" className="profile__edit-button item-animation" onClick={document.querySelector('.popup_type_edit').classList.add('popup_opened')}>
                          <img className="profile__edit-button-icon" src={profileEditButton}
                              alt="Кнопка изменить профиль"/>
                      </button>
                  </div>
                  <p className="profile__discription">Исследователь океана</p>
              </div>
              <button type="button" className="profile__add-button item-animation">
                  <img className="profile__add-button-icon" src={profileAddButton} alt="кнопка добавить пост"/>
              </button>
          </section>
          <section className="elements">
              <ul className="elements__list">

              </ul>
          </section>
        </main>
  );
}

export default Main;
